		    		 		/* 二维码标题（详情） */ 
	    		 			action_info = #{actionInfo,jdbcType=VARCHAR},


		    		 		/* 二维码类型：QR_SCENE：临时整型，QR_STR_SCENE：临时字符串，QR_LIMIT_SCENE：永久整型，
QR_LIMIT_STR_SCENE：永久字符串 */ 
	    		 			action_name = #{actionName,jdbcType=VARCHAR},


		    		 		/* 整型场景值ID */ 
	    		 			scene_id = #{sceneId,jdbcType=INTEGER},


